var Message = require("../Message"),
  fs = require("fs"),
  mime = require("mime");

module.exports = function redirect(fileName, size, lastModified, etag) {
  return new Message(200, fs.createReadStream(fileName), {
    mime: mime.lookup(fileName),
    length: size,
    "last-modified": lastModified.toGMTString(),
    "etag": etag
  });
};