"use strict";

var movedPermanently = require("./handlers/movedPermanently");

module.exports = function (port) {
	return function (request, response) {
		var path = "https://" + request.headers.host;
		if (port !== 443) {
			path += ":" + port;
		}
		path += request.url;
		return movedPermanently(path).send(response);
	};
};